  .services-background-animation path {
  stroke-dasharray: 7050;
  opacity: 10;
  animation: animateBackground 4s;
  }

@keyframes animateBackground {
  0% {
    opacity: 0;
    fill: none;
    stroke-dashoffset: 7050;
  }

  30% {
    opacity: 10;
    fill: none;
    stroke-dashoffset: 6850;
  }

  100% {
    opacity: 10;
    stroke-dashoffset: 0;
  }
}
